import React from "react";
import StripeCheckout from 'react-stripe-checkout';
import axios from 'axios';

const StripeCheckoutButton = ({ price }) => {
    const priceForStripe = price * 100;
    const publishableKey = 'pk_test_51Jz33CL24QvDACf2ydntPpvSTsRdwuAKspXE2AfpUgZXXPynPrHP3NvTk6kJY5tlzJTP3DOXv3X7DDf97CV2kWdl00TOe5q2Pe';

    const onToken = token => {
        axios({
            url: 'payment',
            method: 'post',
            data: {
                amount: priceForStripe,
                token
            }
        }).then(_response => {
            alert('Payment Successful')
        }).catch(error => {
            console.log('Payment error:', JSON.parse(error));
            alert(
                'There was an issue with your payment. please make sure you use the provided credit cart'
            );
        });
    };

    return (
        <StripeCheckout 
            label='Pay Now'
            name='CRWN Clothing Ltd'
            billingAddress
            shippingAddress
            image='https://svgshare.com/i/CUz.svg'
            description={`Your total is €${price}`}
            amount={priceForStripe}
            panelLabel='Pay Now'
            token={onToken}
            stripeKey={publishableKey}
        />
    );
};

export default StripeCheckoutButton;
